SELECT schools.id, schools.name AS school, courses.name AS course, companies.name AS company, roles.name AS role FROM applicants JOIN jobs ON jobs.id = applicants."jobId" JOIN roles ON jobs."roleId" = roles.id JOIN users ON users.id = applicants."userId" JOIN companies ON jobs."companyId" = companies.id JOIN educations ON educations."userId" = users.id JOIN schools ON schools.id = educations."schoolId" JOIN courses ON courses.id = educations."courseId" WHERE roles.id = 2 AND jobs.active = true;
